package com.leetcode.slidingwindow;

import java.util.*;

//https://leetcode.com/problems/find-all-anagrams-in-a-string/
public class FindAllAnagramsInString438 {

    public static void main(String[] args) {
        var res = new FindAllAnagramsInString438();
        System.out.println(res.findAnagrams2("cbaebabacd", "abc"));    //Output: [0,6]
        System.out.println(res.findAnagrams2("abab", "ab"));  //Output: [0,1,2]
        System.out.println(res.findAnagrams2("baa", "aa")); // [1]
        System.out.println(res.findAnagrams2("dinitrophenylhydrazinetrinitrophenylmethylnitramine", "trinitrophenylmethylnitramine"));  // [19,20,21,22]
        System.out.println(res.findAnagrams2("ababababab", "aab")); //[0,2,4,6]
        String case1 =  "";
        System.out.println(res.findAnagrams2(case1, "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz")); //[4000]

    }

    // bae
    // abc

    public List<Integer> findAnagrams2(String s, String p) {
        var result = new LinkedList<Integer>();
        var pCharSum = 0;
        var windowCharSum = 0;
        var baseSet = new HashMap<Character, Integer>();
        var window = new HashMap<Character, Integer>();
        for (char ch : p.toCharArray()) {
            pCharSum += ch;
            baseSet.computeIfPresent(ch, (key, val) -> ++val);
            baseSet.putIfAbsent(ch, 1);
        }
        int i = 1;
        for (char ch : s.toCharArray()) {
            if (baseSet.containsKey(ch)) {
                window.computeIfPresent(ch, (key, val) -> ++val);
                window.putIfAbsent(ch, 1);
            }
            windowCharSum += ch;

            if (i >= p.length()) {
                if (windowCharSum == pCharSum && window.size() == baseSet.size()) {
                    result.add(i - p.length());
                }
                var lookup = s.charAt(i - p.length());
                windowCharSum -= lookup;
                if (window.containsKey(lookup)) {
                    var val = window.get(lookup);
                    window.put(lookup, --val);
                    window.remove(lookup, 0);
                }
            }
            i++;
        }
        return result;
    }


    public List<Integer> findAnagrams(String s, String p) {
        var result = new LinkedList<Integer>();
        var baseSet = new HashMap<Character, Integer>();
        var window = new HashMap<Character, Integer>();
        for (int i = 0; i < p.length(); i++) {
            baseSet.computeIfPresent(p.charAt(i), (key, val) -> ++val);
            baseSet.putIfAbsent(p.charAt(i), 1);
        }
        int i = 1;
        int count = 0;
        for (char ch : s.toCharArray()) {
            if (baseSet.containsKey(ch)) {
                window.computeIfPresent(ch, (key, val) -> ++val);
                window.putIfAbsent(ch, 1);
                if (baseSet.get(ch).equals(window.get(ch))) {
                    count++;
                }
            }

            if (i >= p.length()) {
                if (count == baseSet.size()) {  // contains all - works, but slowly
                    result.add(i - p.length());
                }
                var lookup = s.charAt(i - p.length());
                if (window.containsKey(lookup)) {
                    var val = window.get(lookup);
                    window.put(lookup, --val);
                    if (!baseSet.get(lookup).equals(val)) {
                        count--;
                    }
                }
            }
            i++;
        }
        return result;
    }

}
